import { Component, OnInit } from '@angular/core';
import { EcommerceServiceService } from 'src/app/servicios/ecommerce-service.service';

@Component({
  selector: 'app-servicios-comp',
  templateUrl: './servicios-comp.component.html',
  styleUrls: ['./servicios-comp.component.css']
})
export class ServiciosCompComponent implements OnInit {
  servicios:any


    constructor(private servicio:EcommerceServiceService){

    //////// MUESTRA SERVICIOS ////////
      this.servicio.muestraservicio().subscribe({
        next:(servicios_S)=>{
          this.servicios=servicios_S
        },
        error:(errorData)=>{
          console.error(errorData);
        }
        
      }
         )
    }


    ///////  CODIGO PARA AGREGAR AL CARRITO  ////////
    Snombre:string=""
    agregarNombre(value:string){
      this.Snombre=value

      return this.Snombre
    }

    Smonto:string=""
    agregarMonto(value2:string){
      this.Smonto=value2
      return this.Smonto
    }

    nuevoPedido:any[]=[]
    nuevoServicio:any[]=[]

    nuevoCarrito(){
      /* La idea es guardar en un array los servicios que se van selccionando */ 
      this.nuevoServicio.push(this.Snombre)
      this.nuevoServicio.push(this.Smonto)

      this.nuevoPedido.push(this.nuevoServicio)

      return this.nuevoPedido
    }





    ngOnInit():void{}
}


<div class="div0"></div>
<div class="container">
  
        
    <!--///////////  HERO  ////////////-->
    <!--///////////  HERO  ////////////-->

    <div class="hero grid-container cuerpo banner">
        <div class="row align-items-center hero-sub"> 
            <div class="col"> 
                    <h1 class="encabezado ">Nuestros Servicios</h1>
            </div>
        </div>
    </div>

    <!--///////////  SECCION PRINCIPAL  ////////////-->
    <!--///////////  SECCION PRINCIPAL  ////////////-->
    <!-- se hace la estructura con BOOTSTRAP    -->
    <section class="grid-container">
        <div class="row">
    

        
        <!--    SERVICIOS   -->
    <div class="grid-container col-lg-12 seccion">
        <div class="row justify-content-evenly ">

       <div class="items card cajas card2 "  *ngFor="let servicio of servicios, let i=index"> 
          <img src="../../../assets/cardio.jpg" class="card-img-top" alt="Pilares-tratamiento-diabetes">
          <div class="info card-body">
             <h3  class="card-title">{{servicio.nombre_servicio}}</h3>
             <p >{{servicio.descripcion_servicio}}</p>
             
          </div>
         </div>
            </div>
        </div>


        

    </div>
</section>
