      <section  class="da-sect1 bg-success border border-secondary-subtle">
        
        <form class="form-register  bg-success " [formGroup]="formPOSTRegistroServicio" (submit)="enviarDatosDeServicio()">
          <h3 id="servicios" class="da-sec-t1 logo text-center">Formularios para el registro de servicios</h3>
          <div class="grid-container">
            <div class="row  b-radius border border-secondary-subtle bg-light justify-content-evenly align-items-center p-4 m-4" >


      <!-- INPUTS -->

          <div class="col-lg-3 bg-white  border border-secondary-subtle m-2 p-4">
