import { HttpBackend, HttpClient, HttpClientModule, HttpHeaderResponse, HttpResponse } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { FormBuilder,FormGroup,Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpHeaders } from '@angular/common/http';
import { AuthService } from 'src/app/servicios/auth.service';
import { Emitters } from 'src/app/emitters/emitters';



// SE USARAN FORMULARIOS REACTIVOS Y VALIDACIONES SINCRONICAS

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})

export class LoginComponent implements OnInit{
  //////////////////////////////////////////////////////////
    profileForm:FormGroup | any;



  //////////////////////////////////////////////////////////
    ngOnInit(): void {

        this.profileForm=this.formBuilder.group({
          email:["",[Validators.required,Validators.email]],
          password:["",[Validators.required,Validators.minLength(9)]]
      
        });
    }
  //////////////////////////////////////////////////////////

    constructor(
      private formBuilder:FormBuilder,
      private router:Router,
      private serv_login:AuthService,
      private http : HttpClient){};




  ///// METODO DE SUBMIT DE LOGEO FUNCIONAL SIN CONSUMO DE SERVICIO /////
    submit():void{
      // IF para comprobar si el formulario es valido
          if(this.profileForm.valid){

                  this.http.post('http://localhost:8000/api/auth/login/', this.profileForm.getRawValue(), {withCredentials: true})

                  .subscribe((data) =>{
                  this.router.navigateByUrl("/")
                  console.log("Los datos de logueo son:" + data);
                });                
                
            this.router.navigateByUrl("/auth/registro2usuario")                
                this.profileForm.reset(); // SI VALIDA CORRECTAMENTE SE REINICIAN LOS VALORES DE LOS CAMPOS
          } 
          else{

            // SI NO VALIDA TODOS LOS CAMPOS QUEDAN MARCADO EN ROJO
            this.profileForm.markAllAsTouched();
            alert("No se ingresaron correctamente los datos o no se reconoce el usuario")
          
          }
      }


  ///// METODO DE SUBMIT DE LOGEO CON CONSUMO DE SERVICIO  /////

  submit2():void{
    // IF para comprobar si el formulario es valido
        if(this.profileForm.valid){
              this.serv_login.POST('http://localhost:8000/api/auth/login/',{
                    password:this.profileForm.value.password,
                    email:this.profileForm.value.email,
                  },)
                  .subscribe((data) =>{
                    console.log("Los datos de logueo son:" + data);
                  });  

                  this.router.navigateByUrl("/auth/registro2usuario")
                  this.profileForm.reset(); // SI VALIDA CORRECTAMENTE SE REINICIAN LOS VALORES DE LOS CAMPOS
              } 
        else{

          // SI NO VALIDA TODOS LOS CAMPOS QUEDAN MARCADO EN ROJO
          this.profileForm.markAllAsTouched();
          alert("No se ingresaron correctamente los datos o no se reconoce el usuario")
        
        }
    }


  ///// METODOS GET /////
    get email_GET(){
      return this.profileForm.controls.email;
    }
    get pass_GET(){
      return this.profileForm.controls.password;
    }





}


  <!-- Favicons -->

  <link href="assets/img/favicon.png" rel="icon">


<!--//////////////////////////////    FOMRULARIO    /////////////////////////////////////-->
<!--//////////////////////////////    FOMRULARIO    /////////////////////////////////////-->
<!--//////////////////////////////    FOMRULARIO    /////////////////////////////////////-->

<section class="section-princ" >
<section class="container cont-form text-bg-light text-center my-4 borde shadow-lg form">
  <h1 class=" margin titulo">LOGIN</h1>
  
  <form  [formGroup]="profileForm"  (submit)="submit2()" >

    <!-- ////////////////////////////  EMAIL  //////////////////////////////  -->
    <div class="row g-3 align-items-center  margin campo-input borde campo-email borde_validacion " >
       <!--  label  -->
        <div class="col-lg-3 col-md-12 align-self-start ">
            <label for="exampleFormControlInput1" class="col-form-label sub-campo-input  ">Email</label>
        </div>
        <!--  input  -->
        <div class="col-lg-8 col-md-12 align-self-start  ">
          <input formControlName="email"  type="email" class="form-control sub-campo-input" id="exampleFormControlInput1" placeholder="usuario@ejemplo.com">

        <!-- verificacion -->
            <div *ngIf="username_GET.invalid && (username_GET.dirty || username_GET.touched)"
                class="text-danger msj-verificacion col-lg-12 col-md-12 align-self-start">
            
                    <div *ngIf="username_GET.errors?.['required']">
                      Complete este campo.
                    </div>
                    <div *ngIf="username_GET.errors?.['email']">
                      Ingrese un email con formato correcto.
                    </div>
            </div>      
      
        </div>

    </div>

    <!--  /////////////////////////// CONTRASEÑA ////////////////////////////////   -->
    <div class="row g-3 align-items-center margin  campo-input borde">
       <!--  label  -->
      <div class="col-lg-3 align-self-start ">
        <label for="password" class="col-form-label sub-campo-input">Contraseña</label>
      </div>
        <!--  input   id="inputPassword6" -->
      <div class="col-lg-8 align-self-start ">
        <input formControlName="password" name="" id="password" 
        type="password" class="form-control sub-campo-input" aria-labelledby="passwordHelpInline" placeholder="ejemplocontraseña1234">

          <!--  validacion  -->
        <div *ngIf="pass_GET.invalid && (pass_GET.dirty || pass_GET.touched)"
        class="text-danger">
    
            <div *ngIf="pass_GET.errors?.['required']">
              Complete este campo.
            </div>
            <div *ngIf="pass_GET.errors?.['minlength']">
              Ingrese una contraseña de mas de 9 caracteres.
            </div>
      </div>

      </div>

        
    </div>

    <!--  ////////////////////////////////// BOTONES //////////////////////////////   -->
    <div class="row justify-content-evenly mx-4 ">
      <button type="submit" class="btn btn-primary m-2  col-lg-4">Ingresar</button>
    </div>
      
      
      
      <!-- 
       <button  type="button" class="btn btn-success m-2  col-lg-6" (click)="verificacionLoginAdmin()">
         <a  class="text-white text-decoration-none" >Ingresar como administrador</a>
         </button>
 
 
     -->

    




  </form>
</section>
</section>
